mapper.select:
SELECT A.No, A.Floor, A.Ho, A.InsideTemp, A.SetTemp, A.InHeating, A.OutHeating, A.NowControlValue, A.SetControlValue, DATE_FORMAT(A.RegDate, '%Y-%m-%d %H:%i:%s') AS RegDate, A.NowFlow, A.TotalFlow, (CASE WHEN A.OnOff = 0 THEN 'OFF' WHEN A.OnOff = 1 THEN 'ON' END) AS OnOff, A.TotalHeat, DATE_FORMAT(A.SetDate, '%Y-%m-%d %H:%i:%s') AS SetDate, A.InHeating - A.OutHeating AS DiffHeating FROM RoomC A WHERE A.no = (select max(B.no) from RoomC B where A.Ho = B.Ho group by B.Ho)
AND A.Ho = #{ho}
AND date_format(A.SetDate,'%Y%m%d') = #{regDate}
ORDER BY A.SetDate DESC

SELECT date_format(SetDate,'%H') as HH, round(avg(InsideTemp), 1) as InsideTemp, round(avg(SetTemp), 1) as SetTemp FROM RoomC WHERE date_format(SetDate,'%Y%m%d') = #{regDate}
AND Ho = #{ho}
GROUP BY date_format(SetDate,'%Y%m%d%H')
